INSERT INTO `messages` (`id`, `description`, `cd_message`, `active`, `created_at`, `updated_at`, `client_id`) VALUES
(1, 'Olá, \r\n\r\n Eu serei sua Concierge Digital durante sua permanência no _client_ NOME DO CLIENTE AQUI _client_ . Estarei sempre à disposição para atender suas solicitações.\r\n\r\nPara que eu possa te atender melhor, por favor informe o seu número de atendimento. Você pode encontrá-lo na pulseira de identificação do paciente.', 'init', 1, '2021-11-25 18:08:52', '2021-11-11 18:47:49', 3),
(2, 'Olá, \r\n\r\n Você acessou o canal do *Concierge Digital* do _client_ NOME DO CLIENTE AQUI _client_ .\r\n\r\n Verifiquei que o número de atendimento informado é inválido.\r\n\r\nPara que eu possa te atender melhor, por favor informe o número de atendimento que se encontra na pulseira de identificação do paciente.', 'lobby_attendance_not_found', 1, '2021-11-25 18:08:40', '2021-11-11 18:47:49', 3),
(3, 'Ops! O número informado não é um número de atendimento válido. \n\nPor favor, tente novamente.', 'attendance_invalid', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(4, 'Dei uma conferida aqui e não conseguimos validar sua opção. Por favor, tente novamente.', 'option_invalid', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(5, 'Ops! Ocorreu um erro. Por favor, tente novamente..', 'error', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(6, 'Para que eu possa direcionar melhor o atendimento, por favor me informe se o pedido é para: \n\n *1* - Paciente\n *2* - Acompanhante', 'pac_acomp', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(7, 'Certo, você escolheu: ', 'quantity', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(8, 'Você deseja solicitar mais algum serviço? \n\n *1* - Sim \n *2* - Não, concluir pedido', 'more_service', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(9, 'Para confirmar o pedido, digite: \n *1* - Sim\n *2* - Não, descartar pedido', 'confirm_end_service', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(10, 'Perfeito, pedido enviado.\n\nCaso queria fazer um novo pedido, basta selecionar uma das opções abaixo: \n\n ', 'end_service', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(11, 'Seu pedido foi descartado, mas você pode me chamar novamente selecionando uma das opções abaixo: \n\n', 'cancel_service', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(12, 'É um prazer te atender. \r\nO que você deseja que eu faça por você?', 'submenu', 1, '2021-11-26 14:32:37', '2021-11-11 18:47:49', 3),
(13, 'Ops! O número informado não é um número de atendimento válido. \n\nPor favor, tente novamente.', 'attendance_invalid', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(14, 'Obrigada! \n\n O atendimento por este canal é automático e suas solicitações serão atendidas através do menu de opções. Basta digitar o número da opção desejada:\n\n', 'init_after_service', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(15, 'É um prazer te atender. \r\nO que você deseja que eu faça por você?', 'main_menu', 1, '2021-11-26 14:32:25', '2021-11-11 18:47:49', 3),
(16, 'Caso queria fazer um novo pedido, basta selecionar uma das opções abaixo:\n\n', 'no_default_main_menu', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(17, 'Verifiquei que o número de atendimento informado é inválido.\n\nPara que eu possa te atender melhor, por favor informe o número de atendimento que se encontra na pulseira de identificação do paciente.', 'waiting_attendance_invalid', 1, '2021-11-11 18:47:49', '2021-11-11 18:47:49', 3),
(18, 'Tá certo.\r\n\r\nSegue abaixo o pedido que eu anotei durante nossa conversa.\r\nPor favor, veja se está tudo correto. Assim que confirmar, enviarei para as áreas responsáveis para que atendam dentro do tempo estimado.\r\n', 'title_summary', 1, '2021-12-01 12:34:45', '2021-11-11 18:47:49', 3);
